    const run=useRef(null);
    const [html,setHtml]=useState("");
    const [css,setCss]=useState("");
    const[js,setJS]=useState("");

const handlerun=(e)=>{
(run.current).contentDocument.body.innerHTML=html+"<style>"+css+"</style>";
(run.current).cont
}

  return (
    <div className="editlayout">
        <div className='left'>
            <div className="html"><textarea onChange={(e)=>setHtml(e.target.value)}/></div>
            <div className="css"><textarea  onChange={(e)=>setCss(e.target.value)} /></div>
            <div className="js"><textarea onChange={(e)=>setJS(e.target.value)}/></div>
        </div>
        <div className="right"><button onClick={handlerun}>Run</button><iframe className='codeeditor' ref={run}></iframe></div>
    </div>
  )
}

export default Editor
